memberdef.initializer:
do { \
	try { \
		(*(ArrayColumn<type>*)(itsColumns[whichColumn])).put \
		(rownr, record.asArray##type (whichField)); \
	} \
	catch (const AipsError & e) { \
		(*(ArrayColumn<type>*)(itsColumns[whichColumn])).put \
			(rownr, record.toArray##type (whichField)); \
	} \
} while (0)
